# MOF Screening Configuration Parameters\n\n# General settings\nnum_samples = 1000  # Number of samples to generate\nnum_top_mofs = 10  # Number of top MOFs to screen\n\n# Screening parameters\nproperty = 'energy'  # Property to optimize\nthreshold = -5.0  # Energy threshold in eV\n\n# Output settings\noutput_format = 'csv'  # Format of the output file\noutput_directory = './results'  # Directory to save output files\n\n# Logging settings\nverbose = True  # Enable verbose logging\nlog_file = './logs/screening.log'  # Log file path\n